const projectDatabase=JSON.parse(localStorage.getItem("projectList")),projectItems=projectDatabase.length,projectsEl=document.getElementById("projects"),updateDOM=e=>{e.forEach(function(e){let t="";t+=`\n            <article class="project">\n                <h2 class="project-name">${e.name}</h2>\n                <p class="project-date"><b>Date Completed:</b> ${e.date_completed}</p>\n                <p class="project-tech"><b>Technologies Used:</b> ${e.technologies_used}</p>\n                <p class="project-team"><b>Teammates (if applicable):</b> ${e.teammates}</p>\n                <p class="project-description">${e.description}</p>\n            </article>\n            `,projectsEl.innerHTML+=t})};projectItems>0&&(updateDOM(projectDatabase),document.querySelector("input[name='projectsFilter']").addEventListener("keyup",e=>{if(e.target.value.length>=3){const t=e.target.value.toLowerCase(),a=projectDatabase.filter(e=>e.name.toLowerCase().includes(t)||e.description.toLowerCase().includes(t));0===a.length?projectsEl.innerHTML="\n                        <h3>Search Results Not Found</h3>\n                    ":(projectsEl.innerHTML=" ",updateDOM(a))}}));